import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = (id, data, isLiked) => {
  let liked;
  state.data.map(item => {
    if (item.id === action.payload) {
      liked = !state.isLiked;
    }
  });
  return {
    type: TOGGLE_LIKES,
    payload: liked
  };
};
