import React from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Input
} from "reactstrap";

const InstaCardStyle = styled.div`
  .comment_icons {
    margin-bottom: 10px;
    .comment_icons_heart .far {
      margin-right: 10px;
    }
    .comment_icons_heart .far,
    .comment_icons_comm .far {
      font-size: 25px;
      color: gray;
    }
  }
  .input_comment[type="placeholder"] {
    padding: 100px auto;
  }
`;

const InstaCard = ({
  item: { imageUrl, username, likes, timestamp, thumbnailUrl, comments }
}) => {
  return (
    <InstaCardStyle>
      <Card
        style={{
          marginBottom: 100
        }}
      >
        <CardTitle>{username}</CardTitle>
        <CardImg
          top
          width="100%"
          src={thumbnailUrl}
          alt="Card image cap"
          style={{
            objectFit: "cover"
          }}
        />

        <CardBody>
          <div className="comment_icons">
            <span className="comment_icons_heart">
              <i
                className="far fa-heart"
                style={this.state.isLiked ? { background: "red" } : null}
                onClick={this.isLikedHandler}
              />
            </span>
            <span className="comment_icons_comm">
              <i className="far fa-comment" />
            </span>
          </div>
          <CardSubtitle
            style={{
              fontWeight: "bold",
              margin: "4px 0 10px 0"
            }}
          >
            {likes} likes
          </CardSubtitle>
          {comments.map((comments, index) => (
            <CardText
              key={index}
              style={{
                margin: "6px 0"
              }}
            >
              <span style={{ fontWeight: "bold" }}>{comments.username}</span>{" "}
              <span>{comments.text}</span>
            </CardText>
          ))}
          <span
            style={{
              fontSize: 12,
              color: "gray"
            }}
          >
            {timestamp}
          </span>
        </CardBody>
        <form>
          <Input
            type="text"
            className="input_comment"
            placeholder="Add a comment..."
            style={{
              borderBottom: "none",
              minWidth: "60%",
              borderTopColor: "#DFDFDF",
              fontSize: 14
            }}
          />
        </form>
      </Card>
    </InstaCardStyle>
  );
};

InstaCard.propTypes = {
  instagram: PropTypes.arrayOf(
    PropTypes.shape({
      comments: PropTypes.arrayOf(
        PropTypes.shape({
          text: PropTypes.string,
          username: PropTypes.string
        })
      ),
      thumbnailUrl: PropTypes.string,
      timestamp: PropTypes.string,
      username: PropTypes.string
    })
  )
};

export default InstaCard;
