import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = (id, data) => {
  data = data.map(d => {
    if (id === data.id) {
      return {
        data: [
          ...data,
          {
            likes: data.likes + 1
          }
        ]
      };
    }
  });
  return {
    type: TOGGLE_LIKES,
    payload: data
  };
};
