import data from "../../dummyData";
import { TOGGLE_LIKES, ADD_COMMENT } from "../action/types";
import uuid from "uuid";
const initialState = {
  data
};

export default function instagramReducer(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_LIKES:
      return {
        ...state,
        data: action.newData
      };
    case ADD_COMMENT:
      let data = [...data];
      data = data.map(item => {
        if (item.id === action.id) {
          initialState = {
            data: [
              {
                comments: [
                  {
                    text: action.newText
                  }
                ]
              }
            ]
          };
        }
        return item;
      });
      return {
        ...state,
        data
      };
    default:
      return state;
  }
}
