import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = (id, data) => {
  const newData = data.map(item => {
    if (item.id === id) {
      item.isLiked = !item.isLiked;
      return item;
    }
  });
  return {
    type: TOGGLE_LIKES,
    newData
  };
};
