import data from "../../dummyData";
import { TOGGLE_LIKES } from "../action/types";
const initialState = {
  data,
  isLiked: false
};

export default function instagramReducer(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_LIKES:
      return {
        ...state,
        isLiked: !state.isLiked
      };
    default:
      return state;
  }
}
