import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = (id, data, isLiked) => {
  const newData = [...data];
  newData = newData.map(item => {
    if (item.id === id) {
      return {
        isLiked: !isLiked
      };
    }
  });
  return {
    type: TOGGLE_LIKES,
    payload: newData
  };
};
