import React from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  FormGroup,
  Label,
  Input,
  FormFeedback,
  FormText
} from "reactstrap";

const InstaCardStyle = styled.div`
  .comment_icons {
    margin-bottom: 10px;
    .comment_icons_heart .far {
      margin-right: 10px;
    }
    .comment_icons_heart .far,
    .comment_icons_comm .far {
      font-size: 20px;
      color: gray;
    }
  }
`;

const InstaCard = ({
  item: { imageUrl, username, likes, timestamp, thumbnailUrl, comments }
}) => {
  return (
    <InstaCardStyle>
      <Card
        style={{
          marginBottom: 100
        }}
      >
        <CardTitle>{username}</CardTitle>
        <CardImg
          top
          width="100%"
          src={thumbnailUrl}
          alt="Card image cap"
          style={{
            objectFit: "cover"
          }}
        />

        <CardBody>
          <div className="comment_icons">
            <span className="comment_icons_heart">
              <i className="far fa-heart" />
            </span>
            <span className="comment_icons_comm">
              <i className="far fa-comment" />
            </span>
          </div>
          <CardSubtitle>{likes} likes</CardSubtitle>
          <CardText>
            Some quick InstaCard text to build on the card title and make up the
            bulk of the card's content.
          </CardText>
        </CardBody>
        <form>
          {/* <FormGroup>
          </FormGroup> */}
          <Input placeholder="Add a comment..." />
        </form>
      </Card>
    </InstaCardStyle>
  );
};

InstaCard.propTypes = {
  instagram: PropTypes.arrayOf(
    PropTypes.shape({
      comments: PropTypes.arrayOf(
        PropTypes.shape({
          text: PropTypes.string,
          username: PropTypes.string
        })
      ),
      thumbnailUrl: PropTypes.string,
      timestamp: PropTypes.string,
      username: PropTypes.string
    })
  )
};

export default InstaCard;
