import data from "../../dummyData";
import { TOGGLE_LIKES } from "../action/types";
import uuid from "uuid";
const initialState = {
  data,
  isLiked: false
};

export default function instagramReducer(state = initialState, action) {
  switch (action.type) {
    case TOGGLE_LIKES:
      // console.log(uuid());
      return {
        ...state,
        data: state.data.map(item => {
          return {
            item
          };
        })
      };
    default:
      return state;
  }
}
