import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = id => {
  const toggleLik = data.map(item => {
    if (id === item.id) {
      return {
        isLiked: !isLiked
      };
    }
  });
  return {
    type: TOGGLE_LIKES,
    payload: toggleLik
  };
};
