import { TOGGLE_LIKES } from "./types";

export const onToggleLikesHandler = (id, data, isLiked) => {
  const newData = data.map(item => {
    if (item.id === id) {
      return {
        data: [
          ...item,
          {
            isLiked: !isLiked
          }
        ]
      };
    }
  });
  return {
    type: TOGGLE_LIKES,
    payload: newData
  };
};
